// Basic React bundle placeholder
// This is a minimal build to get Render to deploy
// The actual React bundle would be generated by the build process

console.log('React app loading...');

// Basic error handling
window.addEventListener('error', function(e) {
  console.error('App error:', e);
});

// This is a temporary fix - the actual bundle should come from npm run build
// But for now this allows Render to at least deploy the app structure
document.addEventListener('DOMContentLoaded', function() {
  const root = document.getElementById('root');
  if (root) {
    root.innerHTML = '<div style="padding: 20px; text-align: center;"><h1>Medic4U</h1><p>Loading application...</p><p>Please run "npm run build" in the app-name directory to generate the complete bundle.</p></div>';
  }
});
